import React from "react";

export function ActionBtn(props) {
  const { tweet, action } = props;
  return action.type === "like" ? (
    <button className="btn btn-primary">{tweet.likes} Likes</button>
  ) : (
    <button className="btn btn-primary">{action.type}</button>
  );
}

export function Tweet(props) {
  const tweet = props.tweet;
  const className = props.className;
  return (
    <div className={className}>
      <p className="text-dark">{tweet.context}</p>
      <div className="btn btn-group">
        <ActionBtn tweet={tweet} action={{ type: "like" }} />
        <ActionBtn tweet={tweet} action={{ type: "unlike" }} />
        <ActionBtn tweet={tweet} action={{ type: "retweet" }} />
      </div>
    </div>
  );
}
