import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function listTweet() {
  var list_tweet = tweetListLocation();
  const xhr = new XMLHttpRequest();
  const method = "GET";
  const url = "/tweets/list";
  const responseType = "json";
  xhr.responseType = responseType;
  xhr.open(method, url);

  xhr.onload = function () {
    var tweets = xhr.response;
    var result = "";
    for (let i = 0; i < tweets.length; i++) {
      result += createTweet(tweets[i]);
    }
    list_tweet.innerHTML = result;
  };

  xhr.send();
}

function App() {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    const tweetsItem = [{ context: 123 }, { context: 312 }];
    setTweets(tweetsItem);
  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          {tweets.map((tweet, index) => {
            return <li>{tweet.context}</li>;
          })}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
