import React, { useEffect, useState } from "react";
import { listTweet } from "../lookup/index.js";

export function TweetComponent(props) {
  const [newTweets, setNewTweets] = useState([]);
  const textAreaRef = React.createRef();
  var tempTweet;
  const handleSubmit = (event) => {
    event.preventDefault();
    const newVal = textAreaRef.current.value;
    tempTweet = [...newTweets];
    tempTweet.unshift({
      content: newVal,
      likes: 0,
      id: 2323,
    });
    setNewTweets(tempTweet);
    // console.log("here is the new tweet- ", newTweets, tempTweet);
    textAreaRef.current.value = "";
  };
  return (
    <div>
      <form className="my-5" onSubmit={handleSubmit}>
        <div className="form-group">
          <textarea
            ref={textAreaRef}
            required
            className="form-control"
          ></textarea>
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Tweet Now
          </button>
        </div>
      </form>
      <TweetsList newTweets={newTweets} />
    </div>
  );
}

export function TweetsList(props) {
  const [tweetsInit, setTweetsInit] = useState([]);
  const [tweets, setTweets] = useState([]);
  useEffect(() => {
    let final = [...props.newTweets].concat(tweetsInit);
    if (final.length !== tweets.length) {
      console.log("updating getting tweet-", props.newTweets);
      console.log("here is final- ", final);
      setTweets(final);
    }
  }, [props.newTweets, tweets, tweetsInit]);
  useEffect(() => {
    const myCallback = (response, status) => {
      if (status === 200) {
        setTweetsInit(response);
      } else {
        console.log("possibly you didn't start the django server");
        alert("There was an error, ok-", status);
      }
    };
    listTweet(myCallback);
  }, []);
  return tweets.map((tweet, index) => {
    return (
      <Tweet
        tweet={tweet}
        className="my-5 py-5 border bg-white col-10"
        key={index}
      />
    );
  });
}

export function ActionBtn(props) {
  const { tweet, action } = props;
  // TODO: tweet.userLike is undefined
  const [likes, setLikes] = useState(tweet.likes);
  const [justClicked, setJustClicked] = useState(
    tweet.userLike === true ? true : false
  );
  const handleClick = (event) => {
    event.preventDefault();
    if (action.type === "like") {
      if (justClicked === false) {
        setJustClicked(true);
        setLikes(likes + 1);
      } else {
        setJustClicked(false);
        setLikes(likes - 1);
      }
    }
  };

  const display =
    action.type === "like" ? `${likes} Likes` : `${action.display}`;
  return (
    <button onClick={handleClick} className="btn btn-primary">
      {display}
    </button>
  );
}

export function Tweet(props) {
  const tweet = props.tweet;
  const className = props.className;
  return (
    <div className={className}>
      <p className="text-dark">{tweet.context}</p>
      <div className="btn btn-group">
        <ActionBtn tweet={tweet} action={{ type: "like", display: "Likes" }} />
        <ActionBtn
          tweet={tweet}
          action={{ type: "unlike", display: "Unlike" }}
        />
        <ActionBtn
          tweet={tweet}
          action={{ type: "retweet", display: "Retweet" }}
        />
      </div>
    </div>
  );
}
