import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function listTweet(callback) {
  const xhr = new XMLHttpRequest();
  const method = "GET";
  const url = "http://localhost:8000/api/tweets/list";
  const responseType = "json";
  xhr.responseType = responseType;
  xhr.open(method, url);

  xhr.onload = function () {
    callback(xhr.response, xhr.status);
  };
  xhr.onerror = function (e) {
    callback({ message: "some error" }, 400);
  };
  xhr.send();
}

function ActionBtn(props) {
  const { tweet, action } = props;
  return <button className="btn btn-primary">{tweet.likes} Likes</button>;
}

function Tweet(props) {
  const tweet = props.tweet;
  const className = props.className;
  return (
    <div className={className}>
      <p className="text-dark">{tweet.context}</p>
      <div className="btn btn-group">
        <ActionBtn tweet={tweet} action={"like"} />
      </div>
    </div>
  );
}

function App() {
  const [tweets, setTweets] = useState([]);
  useEffect(() => {
    const myCallback = (response, status) => {
      if (status === 200) {
        setTweets(response);
      } else {
        alert("There was an error");
      }
    };
    listTweet(myCallback);
  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <div>
          {tweets.map((tweet, index) => {
            return (
              <Tweet
                tweet={tweet}
                className="my-5 py-5 border bg-white col-10"
                key={index}
              />
            );
          })}
        </div>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
