import React from "react";

function listTweet(callback) {
  const xhr = new XMLHttpRequest();
  const method = "GET";
  const url = "http://localhost:8000/api/tweets/list";
  const responseType = "json";
  xhr.responseType = responseType;
  xhr.open(method, url);

  xhr.onload = function () {
    callback(xhr.response, xhr.status);
  };
  xhr.onerror = function (e) {
    callback({ message: "some error" }, 400);
  };
  xhr.send();
}

function TweetsList(props) {
  const [tweets, setTweets] = useState([]);
  useEffect(() => {
    const myCallback = (response, status) => {
      if (status === 200) {
        setTweets(response);
      } else {
        alert("There was an error");
      }
    };
    listTweet(myCallback);
  }, []);
  return tweets.map((tweet, index) => {
    return (
      <Tweet
        tweet={tweet}
        className="my-5 py-5 border bg-white col-10"
        key={index}
      />
    );
  });
}

export function ActionBtn(props) {
  const { tweet, action } = props;
  return action.type === "like" ? (
    <button className="btn btn-primary">{tweet.likes} Likes</button>
  ) : (
    <button className="btn btn-primary">{action.type}</button>
  );
}

export function Tweet(props) {
  const tweet = props.tweet;
  const className = props.className;
  return (
    <div className={className}>
      <p className="text-dark">{tweet.context}</p>
      <div className="btn btn-group">
        <ActionBtn tweet={tweet} action={{ type: "like" }} />
        <ActionBtn tweet={tweet} action={{ type: "unlike" }} />
        <ActionBtn tweet={tweet} action={{ type: "retweet" }} />
      </div>
    </div>
  );
}
