import React, { useEffect, useState } from "react";

import { listTweet } from "../lookup/index.js";

export function TweetsList(props) {
  const [tweets, setTweets] = useState([]);
  useEffect(() => {
    const myCallback = (response, status) => {
      if (status === 200) {
        setTweets(response);
      } else {
        alert("There was an error");
      }
    };
    listTweet(myCallback);
  }, []);
  return tweets.map((tweet, index) => {
    return (
      <Tweet
        tweet={tweet}
        className="my-5 py-5 border bg-white col-10"
        key={index}
      />
    );
  });
}

export function ActionBtn(props) {
  const { tweet, action } = props;
  const [likes, setLikes] = useState(tweet.likes);
  const [justClicked, setJustClicked] = useState(
    justClicked === true ? true : false
  );
  const handleClick = (event) => {
    event.preventDefault();
    if (action.type === "like") {
      if (justClicked === false) {
        setJustClicked(true);
        setLikes(likes + 1);
      } else {
        setJustClicked(false);
        setLikes(likes - 1);
      }
    }
  };

  const display =
    action.type === "like" ? `${likes} Likes` : `${action.display}`;
  return (
    <button onClick={handleClick} className="btn btn-primary">
      {display}
    </button>
  );
}

export function Tweet(props) {
  const tweet = props.tweet;
  const className = props.className;
  return (
    <div className={className}>
      <p className="text-dark">{tweet.context}</p>
      <div className="btn btn-group">
        <ActionBtn tweet={tweet} action={{ type: "like", display: "Likes" }} />
        <ActionBtn
          tweet={tweet}
          action={{ type: "unlike", display: "Unlike" }}
        />
        <ActionBtn
          tweet={tweet}
          action={{ type: "retweet", display: "Retweet" }}
        />
      </div>
    </div>
  );
}
